mapper.select:
		select `id`,`url`,`WxName`,`WxId`,`uin`,`key`,`TaskType`,`Price`,`Num`,`TotalMoney`,`taskPeriod`,`Stauts`,`FinishNum`,`sort`,`Createdate`,`Modifydate`,`SettleType`,`settlenum` from wx_taskinfo

		  		   and id = #{id} 
		  		   and url = #{url} 
		  		   and WxName = #{wxname} 
		  		   and WxId = #{wxid} 
		  		   and uin = #{uin} 
		  		   and key = #{key} 
		  		   and TaskType = #{tasktype} 
		  		   and Price = #{price} 
		  		   and Num = #{num} 
		  		   and TotalMoney = #{totalmoney} 
		  		   and taskPeriod = #{taskperiod} 
		  		   and Stauts = #{stauts} 
		  		   and FinishNum = #{finishnum} 
		  		   and sort = #{sort} 
		  		   and Createdate = #{createdate} 
		  		   and Modifydate = #{modifydate} 
			       and settletype = #{settletype} 
				   and settlenum =#{settlenum} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

